import React from "react";
import {
    Button,
    Drawer,
    DrawerBody,
    DrawerCloseButton,
    DrawerContent, DrawerFooter,
    DrawerOverlay,
    useDisclosure
} from "@chakra-ui/react";

function CountryLeagues(){
    const { isOpen, onOpen, onClose } = useDisclosure();
    const btnRef = React.useRef()

    return(
        <>
        <Button bg='transparent'  color='#7b7b7b' mt='50px' onClick={onOpen} ref={btnRef}> Country or League </Button>
        <Drawer
            isOpen={isOpen}
            placement='right'
            onClose={onClose}
            finalFocusRef={btnRef}
        >
            <DrawerOverlay/>
            <DrawerContent>
                <DrawerCloseButton/>
                <DrawerBody>
                    English Premier League / UK
                    La Liga / Spain
                    Ligue 1/ France
                    Bundesliga / Germany
                </DrawerBody>

                <DrawerFooter>
                    <Button variant='outline' mr={3} onClick={onClose}>
                        Cancel
                    </Button>
                </DrawerFooter>
            </DrawerContent>

        </Drawer>
        </>
    )
}

export default CountryLeagues
