import {Container, Heading, ListItem, UnorderedList} from "@chakra-ui/react";
import {useEffect, useState} from "react";

function EndedMatches(){

const [teams, setTeams] = useState(null);

    useEffect(() => {
        const fetching = async () => {
            const url = 'https://sportscore1.p.rapidapi.com/sports/1/events?page=1';
            const options = {
                method: 'GET',
                headers: {
                    'X-RapidAPI-Key': 'cf2e5b804emshefb82ef54fec80dp109415jsne25f7e2abcca',
                    'X-RapidAPI-Host': 'sportscore1.p.rapidapi.com'
                }
            };

            try {
                const response = await fetch(url, options);
                const result = await response.json();
                console.log(result);
                console.log(result.data);

                let dataArray = result.data;

                for(let i = 0; i < 5; i++){
                    console.log(dataArray[i].name);
                    setTeams(dataArray[i].name)
                }


            } catch (error) {
                console.error(error);
            }
        }

        fetching()
    }, []);




    return(
        <Container alignItems='left' bg='pink' minH='100px' minW='100%' mt='60px'>
            <Heading bg='#030303' color='#7b7b7b' size='lg' textAlign='center'>
                Recently Ended Matches
            </Heading>
            <UnorderedList>
                <ListItem>
                    {teams}
                </ListItem>
            </UnorderedList>
        </Container>
    )
}

export default EndedMatches
