import {Container, Heading, ListItem, UnorderedList} from "@chakra-ui/react";
import {useEffect, useState} from "react";

function EndedMatches(){

const [teams, setTeams] = useState(['']);

    useEffect(() => {
        const fetching = async () => {


            const url = 'https://api-football-v1.p.rapidapi.com/v3/fixtures?last=50';
            const options = {
                method: 'GET',
                headers: {
                    'X-RapidAPI-Key': 'cf2e5b804emshefb82ef54fec80dp109415jsne25f7e2abcca',
                    'X-RapidAPI-Host': 'api-football-v1.p.rapidapi.com'
                }
            };

            try {
                const response = await fetch(url, options);
                const result = await response.json();
                console.log(result);

                let dataArray = result.response;
                let ended = [];

                for(let i = 0; i < 5; i++){
                    let homeTeam = dataArray[i].teams.home.name;
                    let awayTeam = dataArray[i].teams.away.name;
                    ended[i] = homeTeam.concat(' - ', awayTeam);
                }

                setTeams(ended)

            } catch (error) {
                console.error(error);
            }

        }

        fetching()
    }, []);




    return(
        <Container alignItems='left' bg='#030303' minH='max-content' minW='100%' mt='60px'>
            <Heading bg='#030303' color='#7b7b7b' size='lg' textAlign='center'>
                Recently Ended Matches
            </Heading>
            <UnorderedList justifyContent='center' listStyleType='none' spacing={5}>
                {teams.map((match) =>
                        <ListItem color='whitesmoke' mt='20px' textAlign='center'>
                            {match}
                        </ListItem>
                )}

            </UnorderedList>
        </Container>
    )
}

export default EndedMatches
