import React, {useEffect, useState} from "react";
import {Container, Heading, ListItem, UnorderedList} from "@chakra-ui/react";

interface test{
    name: string;
}

function LiveMatches(){

    const [ongoing, setOngoing] = useState(['']);

    useEffect(() => {
        const fetching = async () => {
            const url = 'https://sportscore1.p.rapidapi.com/sports/1/events/live?page=1';
            const options = {
                method: 'GET',
                headers: {
                    'X-RapidAPI-Key': 'cf2e5b804emshefb82ef54fec80dp109415jsne25f7e2abcca',
                    'X-RapidAPI-Host': 'sportscore1.p.rapidapi.com'
                }
            };

            try {
                const response = await fetch(url, options);
                const result = await response.json();
                console.log(result);

                let dataArray = result.data;
                let stillOn = [];

                for(let i = 0; i < dataArray.length; i++){
                    stillOn[i] = dataArray[i].name;
                }

                setOngoing(stillOn)

            } catch (error) {
                console.error(error);
            }
        }

        fetching()
    }, []);

    return (
        <Container alignItems='center' bg='#030303' minW='max-content' mt='60px'>
    <Heading bg='#030303' color='#7b7b7b' size='lg' textAlign='center'>
        Live Matches
    </Heading>

    <UnorderedList>
        {ongoing.map((match) =>
            <ListItem>
                {match}
            </ListItem>
        )}
    </UnorderedList>

        </Container>
    )
}

export default LiveMatches

