import React, {useEffect, useState} from "react";
import {Container, Heading, ListItem, UnorderedList} from "@chakra-ui/react";

interface test{
    name: string;
}

function LiveMatches(){

    const [ongoing, setOngoing] = useState(['']);

    useEffect(() => {
        const fetching = async () => {


            const url = 'https://api-football-v1.p.rapidapi.com/v3/fixtures?live=all';
            const options = {
                method: 'GET',
                headers: {
                    'X-RapidAPI-Key': 'cf2e5b804emshefb82ef54fec80dp109415jsne25f7e2abcca',
                    'X-RapidAPI-Host': 'api-football-v1.p.rapidapi.com'
                }
            };

            try {
                const response = await fetch(url, options);
                const result = await response.json();
                console.log(result.response);

                let dataArray = result.response;
                let stillOn = [];

                for(let i = 0; i < 5; i++){
                    let homeT = dataArray[i].teams.home.name;
                    let awayT = dataArray[i].teams.away.name;
                    stillOn[i] = homeT.concat(' - ', awayT);
                }

                setOngoing(stillOn)
                console.log(stillOn)

            } catch (error) {
                console.error(error);
            }

        }

        fetching()
    }, []);

    return (
        <Container alignItems='center' bg='#030303' minW='max-content' mt='60px'>
    <Heading bg='#030303' color='#7b7b7b' size='lg' textAlign='center'>
        Live Matches
    </Heading>

    <UnorderedList justifyContent='center' listStyleType='none' spacing={5}>
        {ongoing.map((match) =>
            <ListItem color='whitesmoke' mt='20px' textAlign='center'>
                {match}
            </ListItem>
        )}
    </UnorderedList>

        </Container>
    )
}

export default LiveMatches

