import {Box, Step, StepDescription, StepIcon, StepIndicator, StepNumber, StepSeparator, StepStatus,
    StepTitle,
    Stepper,
    useSteps,
} from '@chakra-ui/react'



const steps = [
    { title: 'First', description: 'Live Matches' },
    { title: 'Second', description: 'Past Matches' },
    { title: 'Third', description: 'Ended Matches' },
]

function SideStepper() {
    const { activeStep } = useSteps({
        index: 3,
        count: steps.length,
    })

    return (
        <Stepper index={activeStep} orientation='vertical' height='400px' gap='0'>
            {steps.map((step, index) => (
                <Step key={index}>
                    <StepIndicator>
                        <StepStatus
                            complete={<StepIcon />}
                            incomplete={<StepNumber />}
                            active={<StepNumber />}
                        />
                    </StepIndicator>

                    <Box flexShrink='0'>
                        <StepTitle>{step.title}</StepTitle>
                        <StepDescription>{step.description}</StepDescription>
                    </Box>

                    <StepSeparator />
                </Step>
            ))}
        </Stepper>
    )
}

export default SideStepper;
